import React, { Component } from 'react';
import { Layout, Menu } from 'antd';
import { Route, NavLink, Link } from 'react-router-dom';
const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;

const source = [
  { 
    key: 'hello',
    path: '/hello',
    title: 'hello',
    children: [
      {
        key: 'helloList',
        path: '/hello/list',
        title: '列表页',
      }, {
        key: 'helloDetail',
        path: '/hello/detail',
        title: '详情页',
      }
    ]
  }, {
    key: 'hi',
    path: '/hi/1',
    title: 'hi页面'
  }
]

class MSider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inlineCollapsed: false,
    }
  }
  render() {
    const { inlineCollapsed } = this.state;
    const menus = (
      source.map(item => {
        const tMenu = (menu) => {
          if (tMenu.children && tMenu.children.length) {
            return (
              <SubMenu
                key={menu.key}
                title={menu.title}
              >
                {menu.map(cMenu => <Menu.Item key={cMenu.key}>{cMenu.title}</Menu.Item>)}
              </SubMenu>
            )
          } else {
            return (
              <Menu.Item key={menu.key}>{menu.title}</Menu.Item>
            )
          }
        } 
      })
    )

    return (

      <Sider
        width={250}
        style={{ background: '#fff' }}
      >
        <Menu
          style={{ width: 250 }}
          mode="inline"
          inlineCollapsed={inlineCollapsed}
        >
          {source.map(item => {
            const tMenu = (menu) => {
              if (menu.children && menu.children.length) {
                return (
                  <SubMenu
                    key={menu.key}
                    title={menu.title}
                  >
                    {menu.children.map(cMenu => tMenu(cMenu))}
                  </SubMenu>
                )
              } else {
                return (
                  <Menu.Item key={menu.key}><Link to={menu.path}>{menu.title}</Link></Menu.Item>
                )
              }
            };
            return tMenu(item);
          })}
        </Menu>
      </Sider>
    );
  }
}

export default MSider;
