import React from 'react';
import { Provider } from 'react-redux';
import { Router } from 'react-router';
import { syncHistoryWithStore } from 'react-router-redux';
// import { useScroll } from 'react-router-scroll';
import configureStore from './configStore';
import history from './utils/history';
import globalSagas from './containers/App/sagas';
import Routes from './routes';
import { BrowserRouter } from 'react-router-dom';
import Dashboard from './containers/Dashboard';
import { ConnectedRouter } from 'connected-react-router';
import { NavLink } from 'react-router-dom';
const activeLink = {
  color: 'blue',
  fontWeight: 'bold',
}

const initialState = {};
const store = configureStore(initialState, history);
store.runSaga(globalSagas);
// const thistory = syncHistoryWithStore(history, store, {});
function App() {
  return (
    <div className="App">
      <Provider store={store}>
        <Dashboard>
          {/* <ConnectedRouter history={history}> */}
            <BrowserRouter>
              <div>
                <NavLink to="/hello" activeStyle={activeLink}>Hello页面</NavLink><br/>
                <NavLink to="/hello/list" activeStyle={activeLink}>Hello-----list页面</NavLink><br/>
                <NavLink to="/hello/detail" activeStyle={activeLink}>Hello----detail页面</NavLink><br/>
                <NavLink to="/hi" activeStyle={activeLink}>Hi页面</NavLink>
              </div>
              {Routes(store)}
            </BrowserRouter>
          {/* </ConnectedRouter> */}
        </Dashboard>
      </Provider>
    </div>
  );
}

export default App;
