import React, { Component } from 'react';
import Loadable from 'react-loadable';
import { Route, Redirect, Switch } from 'react-router-dom';
import { getAsyncInjectors } from '../utils/asyncInjectors';
import queryString from 'query-string';


import hello from './Hello';
import hi from './Hi';
const modules = {
  menus: [
    hello,
    hi,
  ],
  others: []
};
// const Hello = Loadable({
//    loader: () => import('./containers/Pages/Hello/List'),
//    loading() {
//      return  <div>加载中噢......</div>
//    },
// });
// const Component1 = Loadable({
//   loader: () => import('./Pages/Hello'),
//   loading() {
//     return <div>加载中噢......</div>
//   },
// });

// 为了根据模块名称找到文件
const convFirstChartUpper = (str) => str.substr(0, 1).toUpperCase() + str.substr(1);

export default function CRouter(store) {
  const { injectReducer, injectSagas } = getAsyncInjectors(store);
  return (
    <Switch>
      {
        modules.menus.map(ele => {
            // console.log(ele);
          const route = (ele, parentPath) => {
            console.log(ele);
            const elePath = ele.path ? convFirstChartUpper(ele.path) : '';
            // console.log(elePath);
            const finalPath = `${parentPath || ''}${'/' + elePath}`;
            console.log(`${finalPath}`);
            const Component = Loadable({
              loader: () => import(`../containers/Pages${finalPath}`).then(() => {
                console.log(ele.hasReducer);
                console.log(`../containers/Pages${finalPath}/reducer`)
                // const importModules = Promise.all([
                //   ele.hasReducer && import(`../containers/Pages${finalPath}/reducer`),
                //   // ele.hasSagas && import(`../containers/Pages${finalPath}/sagas`)
                // ])
                // importModules.then(([reducer, sagas]) => {
                //   console.log(reducer);
                //   reducer && injectReducer(finalPath, reducer.default);
                //   // sagas && injectSagas(sagas.default);
                //   console.log(222);
                // })
              }),
              loading() {
                return <div>加载中噢......</div>
              },
            });
            return (
              <Route
                key={ele.path}
                exact={!(ele.child && ele.child.length > 0)}
                path={finalPath}
                render={(props) => {
                  console.log(props);
                  const reg = /\?\S*/g;
                  // 匹配?及其以后字符串
                  const queryParams = window.location.hash.match(reg);
                  // 去除?的参数
                  const { params } = props.match;
                  Object.keys(params).forEach(key => {
                      params[key] = params[key] && params[key].replace(reg, '');
                  });
                  props.match.params = { ...params };
                  const merge = { ...props, query: queryParams ? queryString.parse(queryParams[0]) : {} };
                  console.log(merge);
                  if ( ele.child && ele.child.length > 0 ) {
                    console.log(props.match.path);
                    return (
                      <Component {...props}>
                        <Route path={props.match.path} exact />
                        { ele.child.map((r) => route(r, props.match.path)) }
                      </Component>
                    )
                  }
                  return <Component {...props} />
                }}
              />
            )
          }
            return route(ele);
          })
      }
      <Route render={() => <Redirect to="/404" />} />
    </Switch>
  );
}
