    //   <Breadcrumb.Item key="Dasboard">
    //     <Link to="/">Moon</Link>
    //   </Breadcrumb.Item>
    // ].concat(extraBreadcrumbItems);
    return (
      <Layout style={{ background: '#fff' }}>
        <Header />
        <Layout style={{ background: '#fff' }}>
          <MSider />
          <Layout style={{ background: '#fff' }}>
            <Content
              style={{
                padding: '0 0 24px 24px',
                margin: 0,
                minHeight: 280,
              }}
            >  
              <Bread location={location} />   
              { this.props.children }
            </Content>
          </Layout>
        </Layout>
      </Layout>
    )
  }
}

export default withRouter(Dashboard);
