import React, { Component } from 'react';
import { Layout, Menu, Icon } from 'antd';
import { Route, NavLink, Link } from 'react-router-dom';
const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;

const source = [
  { 
    key: 'hello',
    path: '/hello',
    title: 'hello',
    children: [
      {
        key: 'helloList',
        path: '/hello/list',
        title: '列表页',
        icon: 'dollar',
      }, {
        key: 'helloDetail',
        path: '/hello/detail',
        title: '详情页',
        icon: 'file-text',
      }
    ]
  }, {
    key: 'hi',
    path: '/hi/1',
    title: 'hi页面',
    icon: 'smile',
  }
]

class MSider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inlineCollapsed: false,
    }
  }
  toggleCollapsed = () => {
    this.setState({
      ...this.state,
      inlineCollapsed: !this.state.inlineCollapsed,
    });
  }
  render() {
    const { inlineCollapsed } = this.state;
    return (
      <Sider
        width={250}
        style={{ background: '#fff' }}
      >
        <Menu
          style={{ width: 250 }}
          mode="inline"
          inlineCollapsed={inlineCollapsed}
        >
          {source.map(item => {
            const tMenu = (menu) => {
              if (menu.children && menu.children.length) {
                return (
                  <SubMenu
                    key={menu.key}
                    title={menu.title}
                  >
                    {menu.children.map(cMenu => tMenu(cMenu))}
                  </SubMenu>
                )
              } else {
                return (
                  <Menu.Item key={menu.key}><Link to={menu.path}>{menu.icon && (<Icon type={menu.icon} style={{ verticalAlign: 'middle' }} theme="twoTone"/>)}{menu.title}</Link></Menu.Item>
                )
              }
            };
            return tMenu(item);
          })}
        </Menu>
      </Sider>
    );
  }
}

export default MSider;
