import React, { Component } from 'react';
import Loadable from 'react-loadable';
import { Route, Redirect, Switch } from 'react-router-dom';

import hello from './routes/Hello';
import hi from './routes/Hi';
const modules = {
  menus: [
    hello,
    hi,
  ],
  others: []
};
// const Hello = Loadable({
//    loader: () => import('./containers/Pages/Hello/List'),
//    loading() {
//      return  <div>加载中噢......</div>
//    },
// });
// const Component1 = Loadable({
//   loader: () => import('./Pages/Hello'),
//   loading() {
//     return <div>加载中噢......</div>
//   },
// });

// 为了根据模块名称找到文件
const convFirstChartUpper = (str) => str.substr(0, 1).toUpperCase() + str.substr(1);

export default class CRouter extends Component {
  // 关于权限部分先不写

  render() {

    return (
      <Switch>
        {
            modules.menus.map(ele => {
              console.log(ele);
            const route = (ele, parentPath) => {
              console.log(ele);
              const elePath = ele.path ? convFirstChartUpper(ele.path) : '';
              console.log(elePath);
              // const routePath = `/${parentPath ? convFirstChartUpper(parentPath)+'/' : ''}${elePath}`;
              const finalPath = `/${parentPath ? convFirstChartUpper(parentPath)+'/' : ''}${elePath}`;
              console.log(`${finalPath}`);
              const Component = Loadable({
                loader: () => import(`./containers/Pages${finalPath}`),
                loading() {
                  return <div>加载中噢......</div>
                },
              });
              return (
                <Route
                  key={ele.path}
                  exact
                  path={finalPath}
                  render={(props) => {
                    if ( ele.child && ele.child.length > 0 ) {
                      return (
                        <Component {...props}>
                          { ele.child.map((r) => route(r, ele.path)) }
                        </Component>
                      )
                    }
                    return <Component {...props} />
                  }}
                />
              )
            }
              return route(ele);
            })
        }

        <Route render={() => <Redirect to="/404" />} />
      </Switch>
    )
  }
}
