import React, { Component } from 'react';
import { HashRouter as Router, Route, Switch, Link, withRouter } from 'react-router-dom';
import { Layout } from 'antd';
import { Button, Avatar, Menu, Dropdown, Icon, Breadcrumb, Alert } from 'antd';

import Header from '../../components/Header';
import MSider from '../../components/Sider';
const { Content } = Layout;
const breadcrumbNameMap = {
  '/home': '首页',
  '/hi': 'hi页面',
  '/hello': 'hello页面',
  '/hello/list': 'list页面',
  '/hello/detail': 'detail页面',
}
class Dashboard extends Component {

  render() {
    const { location } = this.props;
    console.log(location);
    const pathSnippets = location.pathname.split('/').filter(i => i);
    const extraBreadcrumbItems = pathSnippets.map((_, index) => {
      const url = `/${pathSnippets.slice(0, index + 1).join('/')}`;
      return (
        <Breadcrumb.Item key={url}>
          { breadcrumbNameMap[url] ? <Link to={url}>{breadcrumbNameMap[url]}</Link> : <span>{breadcrumbNameMap[url]}</span>}
        </Breadcrumb.Item> : ''
      );
    })
    const breadcrumbItems = [
      <Breadcrumb.Item key="Dasboard">
        <Link to="/">Moon</Link>
      </Breadcrumb.Item>
    ].concat(extraBreadcrumbItems);
    return (
      <Layout style={{ background: '#fff' }}>
        <Header />
        <Layout style={{ background: '#fff' }}>
          <MSider />
          <Layout style={{ background: '#fff' }}>
            <Content
              style={{
                padding: '0 0 24px 24px',
                margin: 0,
                minHeight: 280,
              }}
            >  
              <Breadcrumb>{breadcrumbItems}</Breadcrumb>       
              { this.props.children }
            </Content>
          </Layout>
        </Layout>
      </Layout>
    )
  }
}

export default withRouter(Dashboard);
