import React, { Component } from 'react';
import { Layout, Menu } from 'antd';
import { Route, NavLink, Link } from 'react-router-dom';
const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;

const source = [
  { 
    key: 'hello',
    path: '/hello',
    title: 'hello',
    children: [
      {
        key: 'helloList',
        path: '/hello/list',
        title: '列表页',
      }, {
        key: 'helloList',
        path: '/hello/list',
        title: '列表页',
      }
    ]
  }, {
    key: 'hi',
    path: '/hi/1',
    title: 'hi页面'
  }
]

class MSider extends Component {
  render() {
    return (
      <Sider width={250} style={{ background: '#fff' }}>
        <Menu
          style={{ width: 250 }}
          mode="inline"
        >
          <SubMenu
            key="sub1"
            title="Hello"
          >
            <Menu.Item key="5"><Link to="/hello/list" >list页面</Link></Menu.Item>
            <Menu.Item key="6"><Link to="/hello/detail" >detail页面</Link></Menu.Item>
          </SubMenu>
          <Menu.Item><Link to="/hi/1" >hi页面</Link></Menu.Item>
        </Menu>
      </Sider>
    );
  }
}

export default MSider;
